import React from "react";
import Head from "next/head";
import wrapper from "../store/configureStore";
import withReduxSaga from "next-redux-saga";
import CentralAppLayout from "../components/Layout/CentralAppLayout";
import NavibarLayout from "../components/Layout/NavibarLayout";
import useSize from "@react-hook/size";
import { useEffect, useCallback, useRef } from "react";
import { GET_NAVIBAR_SIZE } from "../reducers/menu";
import { useDispatch } from "react-redux";
import { Affix } from "antd";

const App = ({ Component }) => {
  const dispatch = useDispatch();
  const target = useRef(null);
  const [naviHeight, naviWidth] = useSize(target);
  console.log(naviHeight, naviWidth);
  useEffect(() => {
    dispatch({ type: GET_NAVIBAR_SIZE, data: { naviHeight, naviWidth } });
  }, []);
  return (
    <>
      <Head>
        <title>Order</title>
        <meta name="viewport" content="width=device-width, user-scalable=no" />
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/antd/4.2.4/antd.compact.css"
        />
        <script type="text/javascript" src="https://code.jquery.com/jquery-1.12.4.min.js"></script>
        <script
          type="text/javascript"
          src="https://cdn.iamport.kr/js/iamport.payment-1.1.5.js"
        ></script>
        <link
          href="https://fonts.googleapis.com/css2?family=Noto+Sans+KR&display=swap"
          rel="stylesheet"
        />
        <script src="http://dmaps.daum.net/map_js_init/postcode.v2.js?autoload=false"></script>
        <style>
          {`body {
            background-color: #EFF2F5!important;
            font-family: 'Noto Sans KR', sans-serif;
          }`}
        </style>
      </Head>
      <CentralAppLayout>
        <div ref={target}>
          <Affix offset={"top"}>
            <NavibarLayout />
          </Affix>
        </div>
        <Component />
      </CentralAppLayout>
    </>
  );
};

// export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
//   context.store.dispatch({ type: GET_NAVIBAR_SIZE, data: { naviHeight, naviWidth } });
//   context.store.dispatch(END);
//   await context.store.sagaTask.toPromise();
// });

export default wrapper.withRedux(withReduxSaga(App));
