import { List, Avatar, Card, Button } from "antd";
import { useDispatch, useSelector } from "react-redux";

const Cart = () => {
  const { cart } = useSelector((state) => state.cart);
  return (
    <>
      <List
        itemLayout="horizontal"
        dataSource={cart}
        renderItem={(item) => (
          <List.Item>
            <Card style={{ width: "100%" }}>
              <List.Item.Meta
                avatar={
                  <Avatar
                    size={100}
                    shape="square"
                    src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                  />
                }
                title={item.title}
                description={item.price}
              />
            </Card>
          </List.Item>
        )}
      />
      ,
    </>
  );
};

export default Cart;
