import {
  PlusCircleOutlined,
  MinusCircleOutlined,
  MinusOutlined,
  PlusOutlined,
} from "@ant-design/icons";
import { useDispatch } from "react-redux";
import { NEAR_ADD_QUANTITY_REQUEST, NEAR_SUBSTRACT_QUANTITY_REQUEST } from "../../reducers/shop";
import { Col, Row, Tag, Input } from "antd";

const NearAmountToggle = ({ id, quantity }) => {
  console.log(quantity);
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      dispatch({ type: NEAR_ADD_QUANTITY_REQUEST, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: NEAR_SUBSTRACT_QUANTITY_REQUEST, data: { id } });
  };
  return (
    // <Row style={{ textAlign: "center" }}>
    //   <Col span={8}>
    //     <PlusCircleOutlined onClick={onClickAdd} />
    //   </Col>
    //   <Col span={8}>{quantity}</Col>
    //   <Col span={8}>
    //     <MinusCircleOutlined onClick={onClickSubstract} />
    //   </Col>
    // </Row>
    <Row>
      <Input
        style={{
          textAlign: "center",
          verticalAlign: "middle",
        }}
        value={quantity}
        size={"middle"}
        readOnly
        maxLength={1}
        addonBefore={<PlusOutlined onClick={onClickAdd} />}
        addonAfter={<MinusOutlined onClick={onClickSubstract} />}
      />
    </Row>
  );
};

export default NearAmountToggle;
