import { Card, Button, Avatar, Form, Input } from "antd";
import { LeftOutlined } from "@ant-design/icons";
import { useState, useCallback } from "react";
import { useDispatch } from "react-redux";
import { useInput } from "../../components/CustomHooks";
import CentralAppLayout from "../../components/CenteralAppLayout";
import NavibarLayout from "../../components/NavibarLayout";
import { SIGN_IN_REQUEST } from "../../reducers/user";

const Signup = () => {
  const [email, onChangeEmail] = useInput();
  const [password, onChangePassword] = useInput();
  const [passwordCheck, setPasswordCheck] = useState();
  const [passwordError, setPasswordError] = useState(false);
  const [checkbox, onChangeCheckbox] = useState(false);

  const dispatch = useDispatch();

  const onChangePasswordCheck = (e) => {
    setPasswordCheck(e.target.value);
    setPasswordError(password != e.target.value);
  };

  const submitFormTrigger = () => {
    onSubmitForm();
  };

  const onSubmitForm = useCallback(
    (e) => {
      // e.preventDefault();
      console.log(email, password);
      dispatch({
        type: SIGN_IN_REQUEST,
        data: {
          username: email,
          password: password,
        },
      });
    },
    [email, password, checkbox]
  );
  const onSubmit = useCallback(
    (e) => {
      // e.preventDefault();
      console.log(email, password);
      // dispatch({ type: SIGN_UP_REQUEST, data: { email, password } });
    },
    [email, password]
  );
  return (
    <NavibarLayout>
      <CentralAppLayout>
        <Card title={<LeftOutlined />} actions={[<div onClick={submitFormTrigger}>회원가입</div>]}>
          <Form onFinish={onSubmitForm}>
            <Form.Item
              name="email"
              label="Email"
              rules={[
                {
                  type: "email",
                  message: "올바른 이메일 형식을 입력해주세요!",
                },
                {
                  required: true,
                  message: "이메일를 입력해 주세요!",
                },
              ]}
            >
              <Input value={email} onChange={onChangeEmail} />
            </Form.Item>
            <Form.Item
              label="Password"
              name="password"
              rules={[
                {
                  required: true,
                  message: "비밀번호를 입력해 주세요",
                },
              ]}
            >
              <Input.Password value={password} onChange={onChangePassword} />
            </Form.Item>
            <Form.Item>
              <Button type={"primary"} htmlType={"submit"}>
                회원가입
              </Button>
            </Form.Item>
          </Form>
        </Card>
      </CentralAppLayout>
    </NavibarLayout>
  );
};

export default Signup;
