import ProductCard from "../components/ProductCard";
import { useSelector, useDispatch } from "react-redux";
import CentralAppLayout from "../components/CentralAppLayout";
import MenuNavibarLayout from "../components/MenuNavibarLayout";
import { Col, Row, PageHeader } from "antd";

const Index = () => {
  const { products } = useSelector((state) => state.products);

  return (
    <>
      <CentralAppLayout>
        <PageHeader
          className="site-page-header"
          onBack={() => null}
          title="메뉴"
          subTitle="This is a subtitle"
        />
        <MenuNavibarLayout>
          {products &&
            products.map((product) => {
              return (
                <Col sm={12} xs={24}>
                  <ProductCard
                    key={product.id}
                    id={product.id}
                    title={product.title}
                    price={product.price}
                    quantity={product.quantity}
                    cover={product.cover}
                    discount={0.1}
                  />
                </Col>
              );
            })}
        </MenuNavibarLayout>
      </CentralAppLayout>
    </>
  );
};

export default Index;
