import ProductCard from "../components/ProductCard";
import { useSelector, useDispatch } from "react-redux";
import CentralAppLayout from "../components/Layout/CentralAppLayout";
import MenuNavibarLayout from "../components/Layout/MenuNavibarLayout ";
import { Col, Row, PageHeader, Card, Breadcrumb } from "antd";
import Router from "next/router";

const Index = () => {
  const { products } = useSelector((state) => state.shop);

  return (
    <>
      <CentralAppLayout>
        <PageHeader className="site-page-header" onBack={() => null} title="메뉴" />
        <Card title={<MenuNavibarLayout menus={{ menus: [`메인`, `요리`, `주류`] }} />}>
          <Row gutter={16}>
            {products &&
              products.map((product) => {
                return (
                  <Col sm={12} xs={24}>
                    <ProductCard product={product} discount={0.1} />
                  </Col>
                );
              })}
          </Row>
        </Card>
      </CentralAppLayout>
    </>
  );
};

export default Index;
