import ProductCard from "../components/ProductCard";
import { useSelector } from "react-redux";
import CentralAppLayout from "../components/CentralAppLayout";
import NavibarLayout from "../components/NavibarLayout";

const Index = () => {
  const { products } = useSelector((state) => state.products);
  return (
    <>
      <CentralAppLayout>
        <NavibarLayout>
          {products &&
            products.map((product) => {
              return (
                <ProductCard
                  title={product.title}
                  price={product.price}
                  quantity={product.quantity}
                  cover={product.cover}
                />
              );
            })}
        </NavibarLayout>
      </CentralAppLayout>
    </>
  );
};

export default Index;
