import { all, fork } from "redux-saga/effects";
import axios from "axios";
import user from "./user";
import shop from "./shop";

axios.defaults.baseUrl = `http://localhost:3065`;

function* rootSaga() {
  yield all([fork(user), fork(shop)]);
}

export default rootSaga;
