import produce from "immer";

const initialState = {
  lists: [],
  originalLists: [],
};

export const LOAD_LIST_REQUEST = "LOAD_LIST_REQUEST";
export const LOAD_LIST_SUCCESS = "LOAD_LIST_SUCCESS";
export const LOAD_LIST_FAILURE = "LOAD_LIST_FAILURE";

export const PLUS_QUANTITY_SUCCESS = "PLUS_QUANTITY_SUCCESS";

export const MINUS_QUANTITY_SUCCESS = "MINUS_QUANTITY_SUCCESS";

export default (state = initialState, action) => {
  return produce(state, (draft) => {
    switch (action.type) {
      case LOAD_LIST_REQUEST:
        break;
      case LOAD_LIST_SUCCESS:
        draft.lists = action.data.products;
        draft.originalLists = action.data.products;
        break;
      case LOAD_LIST_FAILURE:
        break;

      case PLUS_QUANTITY_SUCCESS:
        const product = draft.lists.find((list) => list.id === action.data.id);
        let quantity = product.quantity;
        const stock = product.stock;
        quantity = quantity < stock ? (quantity += 1) : product.quantity;
        product.quantity = quantity;

        break;

      case MINUS_QUANTITY_SUCCESS:
        {
          let quantity = state.lists[action.data.id].quantity;
          // let quantity = draft.lists[action.data.id].quantity;
          //   let stock = draft.lists[action.data.id].stock;
          quantity = quantity > 1 ? (quantity -= 1) : 1;

          //   quantity < stock ? (quantity += 1) : product.quantity;
          // const index = state.lists.findIndex((product) => product.id === action.data.id);
          // const product = state.lists[index];
          // let quantity = product.quantity;

          // const lists = [...state.lists];
          // lists[index] = { ...product, quantity };
          // return {
          //   ...state,
          //   lists,
          // };
        }
        break;

      default:
        state;
        break;
    }
  });
};
