const initialState = {
  isPayingRequest: false,
  payingRequestSuccess: false,
  payingRequestFailure: false,
  data: null,
  error: null,
  history: [],
  requestingPaymentHistory: false,
};

export const PAYMENT_REQUEST = 'PAYMENT_REQUEST';
export const PAYMENT_SUCCESS = 'PAYMENT_SUCCESS';
export const PAYMENT_FAILURE = 'PAYMENT_FAILURE';

export const PAYMENT_HISTORY_REQUEST = 'PAYMENT_HISTORY_REQUEST';
export const PAYMENT_HISTORY_SUCCESS = 'PAYMENT_HISTORY_SUCCESS';
export const PAYMENT_HISTORY_FAILURE = 'PAYMENT_HISTORY_FAILURE';

export default (state = initialState, action) => {
  switch (action.type) {
    case PAYMENT_REQUEST: {
      return { ...state };
    }
    case PAYMENT_SUCCESS: {
      return { ...state, data: action.data };
    }
    case PAYMENT_FAILURE: {
      return { ...state };
    }

    case PAYMENT_HISTORY_REQUEST: {
      return {
        ...state,
        requestHistory: true,
      };
    }
    case PAYMENT_HISTORY_SUCCESS: {
      return { ...state, history: [...action.data], requestingPaymentHistory: false };
    }
      
    case PAYMENT_HISTORY_FAILURE: {
      return {...state, requestingPaymentHistory: false}
      }
    default: {
      return { ...state };
    }
  }
};
