import { useDispatch, useSelector } from 'react-redux';
import { Form, Button, Input, Slider } from 'antd';
import { DISCOUNT_PRODUCT_REQUEST } from '../../reducers/shop';
import { useState } from 'react';

const DiscountForm = ({ products, product, setDiscount, setProduct }) => {
  const dispatch = useDispatch();
  const [rate, setRate] = useState();
  let ProductIds = [];

  const onChangeRate = (e) => {
    setRate(e);
  };

  const onChangeInputRate = (e) => {
    setRate(e.target.value);
  };

  const onFinish = () => {
    if (product) {
      ProductIds = [{ ProductId: product.id }];
    } else {
      const checkedProduct = products.filter((v) => v.checked == true);
      checkedProduct.map((v) => ProductIds.push({ ProductId: v.id }));
    }

    dispatch({
      type: DISCOUNT_PRODUCT_REQUEST,
      data: {
        ProductIds,
        rate,
      },
    });
    setProduct(null);
    setDiscount(false);
  };

  const onClickDelete = () => {
    if (product) {
      ProductIds = [{ ProductId: product.id }];
    } else {
      const checkedProduct = products.filter((v) => v.checked == true);
      checkedProduct.map((v) => ProductIds.push({ ProductId: v.id }));
    }

    dispatch({
      type: D,
      data: {
        ProductIds,
        rate,
      },
    });
    setProduct(null);
    setDiscount(false);
  }

  return (
    <>
      <Form onFinish={onFinish}>
        <Form.Item>
          <Slider
            value={rate}
            onChange={onChangeRate}
            tooltipVisible
            tipFormatter={(e) => `${e}%`}
          />
          <Input value={rate} onChange={onChangeInputRate} />
        </Form.Item>
        <Button htmlType={'submit'} type={'primary'}>
          등록
        </Button>
      </Form>
    </>
  );
};

export default DiscountForm;
