import React from "react";
// import AppLayout from "../components/AppLayout";
import Head from "next/head";
import PropTypes from "prop-types";
import reducer from "../reducers";
import { Provider, useSelector, useDispatch } from "react-redux";
import { compose, applyMiddleware, createStore } from "redux";
import withRedux from "next-redux-wrapper";
import createSagaMiddleware from "redux-saga";
import rootSaga from "../sagas";

const App = ({ Component, store, pageProps }) => {
  return (
    <Provider store={store}>
      <Head>
        <title>App</title>
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.26.8/antd.css" />
        <script src="https://cdnjs.cloudflare.com/ajax/libs/antd/3.26.8/antd.js"></script>
        <link
          rel="stylesheet"
          type="text/css"
          charset="UTF-8"
          href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css"
        />
        <link
          rel="stylesheet"
          type="text/css"
          href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css"
        />
      </Head>
      {/* <AppLayout> */}
      <Component {...pageProps} />
      {/* </AppLayout> */}
    </Provider>
  );
};

const configureStore = (initialState, options) => {
  const sagaMiddleware = createSagaMiddleware();

  const middleWares = [sagaMiddleware];
  // const enhencer =
  //   process.env.NODE_ENV === "production"
  //     ? compose(applyMiddleware(...middleWares))
  //     : compose(
  //         applyMiddleware(...middleWares),
  //         !options.isServer && window.__REDUX_DEVTOOLS_EXTESION__ !== "undefined"
  //           ? window.__REDUX_DEVTOOLS_EXTENSION__()
  //           : (f) => f
  //       );
  compose(applyMiddleware(...middleWares));
  const store = createStore(reducer, initialState, enhencer);
  // sagaMiddleware.run(rootSaga);
  return store;
};

App.getInitialProps = async (context) => {
  const { ctx, Component } = context;
  let pageProps = {};

  const state = ctx.store.getState();
  const cookie = ctx.isServer ? ctx.req.headers.cookie : "";
  if (Component.getInitialProps) {
    pageProps = await Component.getInitialProps(ctx);
  }
  return { pageProps };
};

App.propTypes = {
  Component: PropTypes.elementType.isRequired,
  store: PropTypes.object.isRequired,
};

export default withRedux(configureStore)(App);
