const initialState = {
  user: null,
  plateNumbers: [],
};

export const GUEST_REGIST_REQUEST = `GUEST_REGIST_REQUEST`;

const reducers = (state = initialState, action) => {
  switch (action.type) {
    case GUEST_REGIST_REQUEST: {
      let id;
      const index = state.plateNumbers.slice(-1)[0];
      index ? (id = index.id + 1) : (id = 1);
      return {
        ...state,
        user: {
          id: 1,
          name: `김호연`,
          phone: `010-2739-0103`,
        },
        plateNumbers: [
          ...state.plateNumbers,
          { id: id, dueDate: action.data.dueDate, plateNumber: action.data.plateNumber },
        ],
      };
    }
    default: {
      return {
        ...state,
      };
    }
  }
};

export default reducers;
