import { useDispatch, useSelector } from 'react-redux';
import { Form, Button, Input } from 'antd';
import { DISCOUNT_PRODUCT_REQUEST } from '../../reducers/shop';

const DiscountForm = ({ products, id, setDiscount }) => {
  const dispatch = useDispatch();
  let ProductIds = [];

  if (products) {
    const checkedProduct = products.filter((v) => v.checked == true);

    checkedProduct.map(
      // (v) => console.log(v.id),
      ProductIds.push(v.id)
    );
  } else {
    ProductIds = [id];
  }

  const onFinish = () => {
    dispatch({
      type: DISCOUNT_PRODUCT_REQUEST,
      ProductIds,
    });
    setDiscount(false);
  };

  return (
    <>
      <Form onFinish={onFinish}>
        <Form.Item>
          <Input></Input>
        </Form.Item>
        <Button htmlType={'submit'} type={'primary'}>
          등록
        </Button>
      </Form>
    </>
  );
};

export default DiscountForm;
