import { fork, takeEvery, all, put, call, takeLatest } from 'redux-saga/effects';
import axios from 'axios';
import {
  LOAD_USER_SUCCESS,
  LOAD_USER_FAILURE,
  LOAD_USER_REQUEST,
  LOG_IN_REQUEST,
  LOG_IN_SUCCESS,
  LOG_IN_FAILURE,
  SIGN_UP_REQUEST,
  SIGN_UP_SUCCESS,
  SIGN_UP_FAILURE,
  BIZCODE_CONFIRM_FAILURE,
  BIZCODE_CONFIRM_SUCCESS,
  BIZCODE_CONFIRM_REQUEST,
  EMAIL_CONFIRM_REQUEST,
  EMAIL_CONFIRM_SUCCESS,
  EMAIL_CONFIRM_FAILURE,
  PASSWORD_CHANGE_REQUEST,
  PASSWORD_CHANGE_SUCCESS,
  PASSWORD_CHANGE_FAILURE,
} from '../reducers/user';

function loginApi(loginData) {
  return axios.post('/user/login', loginData, { withCredentials: true });
}

function* login(action) {
  try {
    const result = yield call(loginApi, action.data);
    yield put({
      type: LOG_IN_SUCCESS,
      data: result.data,
    });
  } catch (e) {
    console.log(e);
    yield put({
      type: LOG_IN_FAILURE,
      error: e,
    });
  }
}

function* watchLogin() {
  console.log('Try logging in');
  yield takeEvery(LOG_IN_REQUEST, login);
}

function signUpApi(signUpData) {
  return axios.post('/user/signup', signUpData);
}

function* signUp(action) {
  try {
    const result = yield call(signUpApi, action.data);
    yield put({
      type: SIGN_UP_SUCCESS,
      data: result.data,
    });
  } catch (e) {
    console.log(e);
    yield put({
      type: SIGN_UP_FAILURE,
      error: e,
    });
  }
}

function* watchSignUp() {
  yield takeEvery(SIGN_UP_REQUEST, signUp);
}

function loadUserApi() {
  // 서버에 요청을 보내는 부분

  return axios.get('/user', { withCredentials: true });
}

function* loadUser() {
  try {
    console.log('load user request');
    const result = yield call(loadUserApi);
    yield put({
      type: LOAD_USER_SUCCESS,
      data: result.data,
    });
  } catch (e) {
    console.log(e);
    yield put({
      type: LOAD_USER_FAILURE,
      error: e,
    });
  }
}

function* watchLoadUser() {
  yield takeEvery(LOAD_USER_REQUEST, loadUser);
}

function changePasswordApi(password) {
  return axios.post('/user/passwordChange', { password });
}

function* passwordChange(action) {
  try {
    const result = yield call(changePasswordApi, action.password);
    yield put({
      type: PASSWORD_CHANGE_SUCCESS,
      data: result.data,
    });
  } catch (e) {
    yield put({
      type: PASSWORD_CHANGE_FAILURE,
      error: e,
    });
  }
}

function* watchPasswordChange() {
  yield takeLatest(PASSWORD_CHANGE_REQUEST, passwordChange);
}

export default function* userSaga() {
  yield all([
    fork(watchLogin),
    fork(watchLoadUser),
    fork(watchSignUp),
    fork(watchBizcodeConfirm),
    fork(watchEmailConfirm),
    fork(watchPasswordChange),
  ]);
}
