import {
  PlusCircleOutlined,
  MinusCircleOutlined,
  MinusOutlined,
  PlusOutlined,
} from "@ant-design/icons";
import { useDispatch } from "react-redux";
import { NEAR_ADD_QUANTITY_REQUEST, NEAR_SUBSTRACT_QUANTITY_REQUEST } from "../../reducers/shop";
import { Col, Row, Typography, Input } from "antd";
import product from "../../../backend/models/product";
import NumberFormat from "react-number-format";

const NearAmountToggle = ({ id, quantity, product, discount }) => {
  console.log(quantity);
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      dispatch({ type: NEAR_ADD_QUANTITY_REQUEST, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: NEAR_SUBSTRACT_QUANTITY_REQUEST, data: { id } });
  };
  return (
    // <Row style={{ textAlign: "center" }}>
    //   <Col span={8}>
    //     <PlusCircleOutlined onClick={onClickAdd} />
    //   </Col>
    //   <Col span={8}>{quantity}</Col>
    //   <Col span={8}>
    //     <MinusCircleOutlined onClick={onClickSubstract} />
    //   </Col>
    // </Row>
    <Row>
      <Col span={12}>
        <Typography.Text style={{ textDecoration: "line-through" }}>
          <NumberFormat
            value={discount ? product.price * ((100 - discount) * 0.01) : product.price}
            displayType={"text"}
            thousandSeparator={true}
          />
          Ïõê
        </Typography.Text>
      </Col>
      <Col span={12}>
        <Input
          style={{
            marginTop: "10%",
            textAlign: "center",
            verticalAlign: "middle",
            width: "100%",
          }}
          value={quantity}
          size={"small"}
          readOnly
          maxLength={1}
          addonBefore={<PlusOutlined onClick={onClickAdd} />}
          addonAfter={<MinusOutlined onClick={onClickSubstract} />}
        />
      </Col>
    </Row>
  );
};

export default NearAmountToggle;
