import { useRouter } from "next/router";
import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { Col, Card, Row, Typography } from "antd";
import {} from "@ant-design/icons";

const Index = () => {
  const router = useRouter();
  const { query } = router;
  const { session } = useSelector((state) => state.user);
  const { Shops } = session;
  const ShopInfo = Shops.filter((shop) => shop.id === query.ShopId);
  return (
    <>
      <Card>
        <Row gutter={[10, 10]}>
          <Col span={24}>
            <Card
              title={<Typography.Title level={3}>상품관리</Typography.Title>}
              onClick={() => router.push(`/shop/admin/${query.ShopId}/product`)}
            ></Card>
          </Col>
          <Col span={24}>
            <Card
              title={<Typography.Title level={3}>매출관리</Typography.Title>}
              onClick={() => router.push(`/payments`)}
            ></Card>
          </Col>
        </Row>
      </Card>
    </>
  );
};

export default Index;
