import { useRouter } from "next/router";
import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { Col, Card, Row, Typography } from "antd";
import {} from "@ant-design/icons";

const Index = () => {
  const router = useRouter();
  const { query } = router;
  const { session } = useSelector((state) => state.user);
  const { Shops } = session;
  const ShopInfo = Shops.filter((shop) => {
    console.log(shop.id == query.ShopId);
    return shop.id == query.ShopId;
  });
  console.log(ShopInfo && ShopInfo.name);
  return (
    <>
      <Card title={<Typography.Title>{ShopInfo && ShopInfo[0].name}</Typography.Title>}>
        <Row gutter={[10, 10]}>
          <Col span={24}>
            <Card
              title={<Typography.Title level={3}>상품관리</Typography.Title>}
              onClick={() => router.push(`/shop/admin/${query.ShopId}/product`)}
            ></Card>
          </Col>
          <Col span={24}>
            <Card
              title={<Typography.Title level={3}>매출관리</Typography.Title>}
              onClick={() => router.push(`/shop/admin/history`)}
            ></Card>
          </Col>
        </Row>
      </Card>
    </>
  );
};

export default Index;
