import ProductCard from "../components/ProductCard";
import { useSelector, useDispatch } from "react-redux";
import CentralAppLayout from "../components/CentralAppLayout";
import NavibarLayout from "../components/NavibarLayout";
import { Col, Row } from "antd";

const Index = () => {
  const { products } = useSelector((state) => state.products);

  return (
    <>
      <CentralAppLayout>
        <NavibarLayout>
          {products &&
            products.map((product) => {
              return (
                <Row gutter={15}>
                  <Col span={10}>
                    <ProductCard
                      key={product.id}
                      id={product.id}
                      title={product.title}
                      price={product.price}
                      quantity={product.quantity}
                      cover={product.cover}
                    />
                  </Col>
                </Row>
              );
            })}
        </NavibarLayout>
      </CentralAppLayout>
    </>
  );
};

export default Index;
