import { List, Avatar, Card, Button, Row, Col, Divider, Empty } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { RestOutlined } from '@ant-design/icons';
import CartAmountToggle from '../../components/Generalui/CartAmoutToggle';
import NumberFormat from 'react-number-format';
import { REMOVE_ITEM_REQUEST } from '../../reducers/cart';
import {useState, useEffect} from 'react'
import { useWindowSize } from '@react-hook/window-size';

const Cart = () => {
  const dispatch = useDispatch();
  const { cart } = useSelector((state) => state.cart);
  const [windowWidth, windowHeight] = useWindowSize();

  useEffect(() => {
    const { IMP } = window;
    setIMP(IMP);
    dispatch({
      type: LOAD_PRODUCTS_REQUEST,
      data: { shopId: query.shopId, tableId: query.tableId },
    });
  }, []);


  const onClickPayment = () => {
    if (isLoggedIn) {
      onWindowScroll();
      if (IMP !== null) {
        IMP.init('imp37098043');
        const today = Date.now();
        IMP.request_pay(
          {
            pay_method: 'card',
            name: product.title + '-' + product.quantity + '-' + product.price,
            Merchant_uid: 'uid' + today,
            amount: product.discount
              ? product.price * (1 - product.discount) * product.quantity
              : product.price * product.quantity,
            UserId: session.id,
            ProductId: product.id,
            ShopId: product.Shop.id,
          },
          callback,
        );
      }
    } else {
      onChangeToggleModal();
    }
  };

  const callback = async (response) => {
    const { success, error_msg } = response;
    if (success) {
      axios
        // .post(`${hostIp}/payments/complete/${product.id}/${product.Shop.id}/${session.id}`, { data: response })
        .post(
          `http://127.0.0.1:3065/payments/complete/${product.id}/${product.Shop.id}/${session.id}`,
          { data: response },
        )
        .then((response) => {
          dispatch({ type: PAYMENT_SUCCESS }, { data: response });
        })
        .catch((e) => {
          console.log(e);
          dispatch({ type: PAYMENT_FAILURE }, { error: e });
        });
    } else {
      alert(`결제 실패: ${error_msg}`);
    }
  };

  return (
    <>
      {cart.length === 0 ? (
        <Card style={{ height: '100vh' }}>
          <div style={{ marginTop: windowHeight * 0.3, textAlign: 'center' }}>
            <Empty />
            <h1>장바구니가 비었습니다</h1>
          </div>
        </Card>
      ) : (
        <List
          itemLayout="horizontal"
          dataSource={cart}
          renderItem={(item) => (
            <List.Item>
              <Card
                title={<h1>{item.title}</h1>}
                style={{ width: '100%' }}
                extra={
                  <h2>
                    <RestOutlined
                      onClick={() => {
                        dispatch({ type: REMOVE_ITEM_REQUEST, id: item.id });
                      }}
                    />
                  </h2>
                }
              >
                <Card.Meta
                  description={
                    <List.Item.Meta
                      avatar={
                        <Avatar
                          size={100}
                          shape="square"
                          src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                        />
                      }
                      description={
                        <Row Gutter={[8, 16]}>
                          <Col span={24}>{item.description}</Col>
                          <Col span={16}>
                            가격:{' '}
                            <NumberFormat
                              value={item.price}
                              displayType={'text'}
                              thousandSeparator={true}
                            />
                          </Col>
                          <Col span={8}>
                            <CartAmountToggle id={item.id} quantity={item.quantity} />
                          </Col>
                        </Row>
                      }
                    />
                  }
                />
                <Divider />
                <Row gutter={[8, 16]}>
                  <Col style={{ textAlign: 'right' }} span={24}>
                    총액:{' '}
                    <NumberFormat
                      value={item.price * item.quantity}
                      displayType={'text'}
                      thousandSeparator={true}
                    />
                  </Col>
                </Row>
              </Card>
            </List.Item>
          )}
        />
      )}
    </>
  );
};

export default Cart;
