import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useRouter } from 'next/router';

import { Empty, Card, Space, Tag, Button } from 'antd';
import { ADMIN_PRODUCTS_REQUEST } from '../../../reducers/shop';

const Index = () => {
  const dispatch = useDispatch();
  const { session, division } = useSelector((state) => state.user);

  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  const { products, categories } = useSelector((state) => state.shop);

  const router = useRouter();

  if (division === false || session === undefined) {
    router.back();
  }

  console.log(categories);

  return (
    <>
      <Space direction={'vertical'} style={{ width: '100%' }}>
        <Card
          title={'카테고리 관리'}
          extra={
            categories && categories.length < 6 ? (
              <Button>카테고리 추가</Button>
            ) : (
              <div>카테고리는 최대 5개까지 설정하실 수 있습니다.</div>
            )
          }
        >
          {categories.length > 0 ? (
            categories.map((v) => <Tag>{v.name}</Tag>)
          ) : (
            <Empty>카테고리를 추가해주세요.</Empty>
          )}
        </Card>
        {products.length > 0 && (
          <Card title={'상품정보'}
            cover={`http://127.0.0.1:3065/uploads/products[0]`}
            onClick={() => router.push('/shop/admin/product')}>
            <Card.Meta
              description={
                products.length > 1
                  ? `${products[0].title} 외 ${products.length - 1}개의 상품이 존재하고 있습니다.`
                  : `${products[0].title} 상품이 존재하고 있습니다.`
              }
            />
          </Card>
        )}
      </Space>
    </>
  );
};

export default Index;
