import { List, Button, Avatar } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import PropTypes from "prop-types";
import { LOAD_PRODUCTS_REQUEST } from "../../../reducers/shop";

const Console = () => {
  const dispatch = useDispatch();
  const { session } = useSelector((state) => state.user);
  console.log(session);
  useEffect(() => {
    dispatch({ type: LOAD_PRODUCTS_REQUEST, data: session.id });
  }, []);
  const { products } = useSelector((state) => state.shop);
  console.log(products);
  return <div>test</div>;
};

Console.propTypes = {};

Console.getInitialProps = async (context) => {
  const items = await context.store.dispatch({ type: LOAD_PRODUCTS_REQUEST });
  return items;
};

export default Console;
