import { Card, Avatar, Button, Col, Row, Descriptions, Space } from "antd";
import { useDispatch, useSelector } from "react-redux";
import {
  ADD_QUANTITY_SUCCESS,
  ADD_QUANTITY_REQUEST,
  SUBSTRACT_QUANTITY_SUCCESS,
} from "../reducers/products";
import NumberFormat from "react-number-format";
import Strike from "react-strike";

const ProductCard = ({ id, title, cover, price, quantity, discount }) => {
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      dispatch({ type: ADD_QUANTITY_SUCCESS, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: SUBSTRACT_QUANTITY_SUCCESS, data: { id } });
  };
  return (
    <Card
      key={id}
      title={title}
      actions={[<div>즉시 구매</div>, <div>장바구니</div>]}
      cover={<img src={cover} />}
    >
      <Card.Meta
        description={
          <>
            {discount ? (
              <>
                <h1 style={{ color: "#FF5733", fontWeight: "bold" }}>
                  <NumberFormat
                    value={price * (1 - discount)}
                    displayType={"text"}
                    thousandSeparator={true}
                  />
                </h1>
                <strike>
                  <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />
                </strike>
              </>
            ) : (
              <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />
            )}

            <Button shape="circle" size="small" onClick={onClickAdd}>
              +
            </Button>
            <NumberFormat value={quantity} displayType={"text"} thousandSeparator={true} />
            <Button shape="circle" size="small" onClick={onClickSubstract}>
              -
            </Button>
            <NumberFormat value={price * quantity} displayType={"text"} thousandSeparator={true} />
          </>
        }
      />
    </Card>
  );
};

export default ProductCard;
