import { Input, Card, Button, Space, Form, PageHeader } from "antd";
import { useInput } from "../../components/Generalui/CustomHooks";
import { useDispatch, useSelector } from "react-redux";
import { useCallback } from "react";
import Link from "next/link";
import Router from "next/router";
import { MailOutlined, LockOutlined } from "@ant-design/icons";
import wrapper from "../../store/configureStore";
import axios from "axios";
import { END } from "redux-saga";
import { LOAD_USER_REQUEST, LOG_IN_REQUEST } from "../../reducers/user";

const Login = () => {
  const [username, onChangeUsername] = useInput();
  const [password, onChangePassword] = useInput();
  const dispatch = useDispatch();

  const onFinish = useCallback(async () => {
    await dispatch({
      type: LOG_IN_REQUEST,
      data: {
        username,
        password,
      },
    });
    const { isLoggedIn } = await useSelector((state) => state.user);
    console.log(`isLoggedIn: ${isLoggedIn}`);
    // console.log(isLoggedIn);
    if (isLoggedIn) {
      Router.push("/");
    }
  }, [username, password, isLoggedIn]);

  return (
    <>
      <Card style={{ height: "100vh", verticalAlign: "middle" }}>
        <Form onFinish={onFinish}>
          <Space direction="vertical" size={"large"} style={{ width: "100%" }}>
            <h1>Login</h1>
            <Form.Item
              name={"email"}
              rules={[
                { required: true, message: "이메일을 입력해주세요" },
                { type: "email", message: "형식에 맞지 않는 아이디입니다" },
              ]}
            >
              <Input
                prefix={<MailOutlined />}
                size={"large"}
                name={"email"}
                value={username}
                onChange={onChangeUsername}
              />
            </Form.Item>
            <Form.Item
              name={"password"}
              rules={[
                { required: true, message: "비밀번호를 입력해주세요" },
                { pattern: /^[0-9a-zA-Z]{6,12}$/, message: "형식에 맞지 않는 비밀번호입니다" },
              ]}
            >
              <Input.Password
                name={"password"}
                value={password}
                prefix={<LockOutlined />}
                size={"large"}
                onChange={onChangePassword}
              />
            </Form.Item>
            <Button type={"primary"} htmlType={"submit"} style={{ width: "100%" }}>
              Login
            </Button>
            <Link href={"/user/signup"}>
              <a>
                <Button style={{ width: "100%" }}>회원가입</Button>
              </a>
            </Link>
          </Space>
        </Form>
      </Card>
    </>
  );
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
  const cookie = context.req ? context.req.headers.cookie : "";
  axios.defaults.headers.Cookie = cookie;
  const result = await context.store.dispatch({ type: LOAD_USER_REQUEST });
  context.store.dispatch(END);
  await context.store.sagaTask.toPromise();
  const state = await context.store.getState();
  console.log(state.user);
  const { session, isLoggedIn } = state.user;
  if (session) {
    context.res.setHeader("location", "/");
    context.res.statusCode = 302;
    context.res.end();
  }
});

export default Login;
