import { List, Button, Avatar } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import PropTypes from "prop-types";
import { ADMIN_PRODUCTS_REQUEST } from "../../../reducers/shop";

const Console = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch({ type: ADMIN_PRODUCTS_REQUEST });
  }, []);
  const { productList } = useSelector((state) => state.shop);
  console.log(productList[0] && productList[0].Images);
  return (
    <>
      {productList[0] && (
        <Avatar
          src={`http://127.0.0.1:3065/uploads/${productList[0].Images[0].url}`}
          size={{ md: 40 }}
          shape={"square"}
        />
      )}

      <List
        dataSource={productList}
        renderItem={(item) => {
          return (
            <List.Item key={item.id}>
              <List.Item.Meta
                avatar={
                  <Avatar
                    src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                    shape="circle"
                    size={"small"}
                  />
                }
                title={item.title}
                description={item.description}
              />
            </List.Item>
          );
        }}
      />
    </>
  );
};
export default Console;
