import { Card, Col, Space, Row, Divider } from "antd";
import NumberFormat from "react-number-format";
import AmountToggle from "./Generalui/AmountToggle";

const ProductCard = ({ product, discount }) => {
  return (
    <Card
      key={product.id}
      title={
        <>
          <h1 style={{ display: "inline" }}>{product.title}</h1>
        </>
      }
      actions={[<div>즉시 구매</div>, <div>장바구니</div>]}
      cover={<img src={product.cover} />}
      hoverable
    >
      <Card.Meta
        title={<Divider />}
        description={
          <>
            <Row align={"bottom"}>
              <Space direction="horizontal">
                <Col md={16} xs={16}>
                  {discount ? (
                    <>
                      <h1 style={{ color: "#FF5733", fontWeight: "bold", display: "inline-block" }}>
                        <NumberFormat
                          value={product.price * (1 - discount)}
                          displayType={"text"}
                          thousandSeparator={true}
                        />
                        원
                      </h1>
                      {"  "}
                      <h3 style={{ color: "#AA9C99", display: "inline-block" }}>
                        {(1 - discount) * 100}%
                      </h3>
                    </>
                  ) : (
                    <>
                      <h1>
                        <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />
                        원
                      </h1>
                    </>
                  )}{" "}
                </Col>
                <Col md={8} xs={8}>
                  <AmountToggle id={product.id} quantity={product.quantity} />
                </Col>
              </Space>
            </Row>
            <br />
            <Row style={{ textAlign: "right" }}>
              <h1>
                <NumberFormat
                  value={
                    discount
                      ? product.price * (1 - discount) * product.quantity
                      : product.price * product.quantity
                  }
                  displayType={"text"}
                  thousandSeparator={true}
                />
                원
              </h1>
            </Row>
          </>
        }
      />
    </Card>
  );
};

export default ProductCard;
