import { Card, Button, Avatar, Form, Input } from "antd";
import { LeftOutlined } from "@ant-design/icons";
import { useState } from "react";
import { useInput } from "../../components/customHooks";
import CentralAppLayout from "../../components/CenteralAppLayout";

const Signup = () => {
  const [email, onChangeEmail] = useInput();
  const [password, onChangePassword] = useInput();
  const [passwordCheck, setPasswordCheck] = useState();
  const [passwordConfirm, setPasswordConfirm] = useState(false);
  const [checkbox, onChangeCheckbox] = useState(false);

  const onChangePasswordCheck = (e) => {
    setPasswordCheck(e.target.value);
    setPasswordConfirm(password != e.target.value);
  };

  const hello = () => {
    console.log("hello");
  };

  return (
    <CentralAppLayout>
      <Card title={<LeftOutlined />} actions={[<div onClick={hello}>회원가입</div>]}>
        <Form>
          <Form.Item
            name="email"
            label="E-mail"
            rules={[
              {
                type: "email",
                message: "The input is not valid E-mail!",
              },
              {
                required: true,
                message: "Please input your E-mail!",
              },
            ]}
          >
            <Input value={email} onChange={onChangeEmail} />
          </Form.Item>
          <Form.Item
            label="Password"
            name="password"
            rules={[
              {
                required: true,
                message: "Please input your password!",
              },
            ]}
          >
            <Input.Password value={password} onChange={onChangePassword} />
          </Form.Item>
          <Form.Item
            name="passwordCheck"
            label="password check"
            rules={[
              {
                required: true,
                message: "Please input your password check",
              },
            ]}
          >
            <Input.Password value={passwordCheck} onChange={onChangePasswordCheck} />
          </Form.Item>
        </Form>
      </Card>
    </CentralAppLayout>
  );
};

export default Signup;
