import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Link from 'next/link';
import { useRouter } from 'next/router';

import { Menu, Card } from 'antd';
import { ADMIN_PRODUCTS_REQUEST } from '../../../reducers/shop';

const Index = () => {
  const dispatch = useDispatch();
  const { session, division } = useSelector((state) => state.user);

  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  const { products } = useSelector((state) => state.shop);

  const router = useRouter();

  if (division === false || session === undefined) {
    router.back();
  }

  return (
    <>
      <Card style={{ height: '100vh' }}>
        {products.length > 0 && (
          <Card title={'상품정보'}>
            <Card.Meta
              onClick={() => router.push('/shop/admin/product')}
              description={
                products.length > 1 &&
                `
              ${products[0].title} 외
              ${products.length - 1}개의 상품이 존재하고 있습니다.`}
            />
          </Card>
        )}
      </Card>
    </>
  );
};

export default Index;
