import { Menu, Input, Modal } from 'antd';
import Link from 'next/link';
import { ArrowLeftOutlined, UserOutlined } from '@ant-design/icons';
import { useSelector, useDispatch } from 'react-redux';
import Router from 'next/router';
import { LOG_OUT_SUCCESS } from '../../reducers/user';
import { useState } from 'react';
import LoginModal from '../Generalui/Login';

const NavibarLayout = () => {
  const [toggleLogin, onChangeToggleLogin] = useState(false);

  const dispatch = useDispatch();
  const onClickLogout = () => {
    dispatch({ type: LOG_OUT_SUCCESS });
  };

  const toggleLoginModal = () => {
    onChangeToggleLogin((prev) => !prev);
  };

  <Modal title={'Login'} visible={toggleLogin} onCancel={toggleLoginModal} footer={null}>
    <LoginModal />
  </Modal>;

  const { isLoggedIn } = useSelector((state) => state.user);

  return (
    <div style={{ marginTop: '10px', backGroundColor: 'white' }}>
      <Menu mode="horizontal">
        <Menu.Item>
          <ArrowLeftOutlined onClick={() => Router.back()} />
        </Menu.Item>
        {!isLoggedIn
          ? [
              <Menu.Item style={{ float: 'right' }} key={'login'}>
                <a onClick={toggleLoginModal}>로그인</a>
              </Menu.Item>,
            ]
          : [
              <Menu.Item style={{ float: 'right' }} key={'logout'}>
                <a onClick={onClickLogout}>로그아웃</a>
              </Menu.Item>,
              <Menu.Item key={'profile'} style={{ float: 'right' }} key={'profile'}>
                <Link href={'/user/profile'}>
                  <a>
                    <UserOutlined />
                  </a>
                </Link>
              </Menu.Item>,
            ]}
        <Menu.Item key={'search'} style={{ float: 'right' }} key={'search'}>
          <Input.Search enterButton style={{ verticalAlign: 'middle' }} size={`large`} />
        </Menu.Item>
      </Menu>
    </div>
  );
};

export default NavibarLayout;
