import React from "react";
import Dropzone from "react-dropzone";
import Cropper from "react-easy-crop";
import { useState, useCallback } from "react";
import { useSelector } from "react-redux";
import { Button } from "antd";

const ImageInput = () => {
  // const { imagePath } = useSelector((state) => state.product);

  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [rotation, setRotation] = useState(0);
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  const [croppedImage, setCroppedImage] = useState(null);

  const onCropComplete = useCallback((croppedArea, croppedAreaPixels) => {
    console.log(croppedArea, croppedAreaPixels);
  }, []);

  const [upImg, setUpImg] = React.useState(null);

  return (
    <>
      <Dropzone
        onDrop={async (imgFile) => {
          await setUpImg(imgFile);
        }}
      >
        {({ getRootProps, getInputProps }) => (
          <section>
            <div {...getRootProps()}>
              <input {...getInputProps()} />
              <p>Drag 'n' drop some files here, or click to select files</p>
            </div>
          </section>
        )}
      </Dropzone>
      {upImg && (
        <>
          <Cropper
            image={`https://images.mypetlife.co.kr/content/uploads/2019/09/04222847/dog-panting.jpg`}
            crop={crop}
            zoom={zoom}
            onCropChange={onCropChange}
            onZoomChange={onZoomChange}
            onMediaLoaded={() => {
              onZoomChange(1);
            }}
            onCropcomplete={onCropComplete}
          />
          <Button>test</Button>
        </>
      )}
    </>
  );
};

export default ImageInput;
