import { List, Avatar, Card, Button, Row, Col, Divider, Empty } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { RestOutlined } from '@ant-design/icons';

import NumberFormat from 'react-number-format';
import { useState, useEffect } from 'react';
import { useWindowSize } from '@react-hook/window-size';
import axios from 'axios';
import { useRouter } from 'next/router';
import {
  CLIENT_HISTORY_REMOVE_REQUEST,
  LOAD_PAYMENT_HISTORY_REQUEST,
} from '../../reducers/payment';

const Cart = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch({ type: LOAD_PAYMENT_HISTORY_REQUEST, division: session.division, id: session.id });
  }, []);

  const { isLoggedIn, session } = useSelector((state) => state.user);
  const { history } = useSelector((state) => state.payment);

  const [windowWidth, windowHeight] = useWindowSize();
  const router = useRouter();

  const onWindowScroll = () => {
    window.scrollTo({ top: 0 });
  };

  return (
    <>
      {cart.length === 0 ? (
        <Card style={{ height: '100vh' }}>
          <div style={{ marginTop: windowHeight * 0.3, textAlign: 'center' }}>
            <Empty />
            <h1>거래내역이 존재하지 않습니다</h1>
          </div>
        </Card>
      ) : (
        <List
          itemLayout="horizontal"
          dataSource={history}
          renderItem={(item) => (
            <List.Item>
              <Card
                title={<h1>{item.Product.title}</h1>}
                style={{ width: '100%' }}
                extra={
                  <h2>
                    <RestOutlined />
                    "환불자리"
                  </h2>
                }
              >
                <Card.Meta
                  description={
                    <List.Item.Meta
                      avatar={
                        <Avatar
                          size={100}
                          shape="square"
                          src={`http://127.0.0.1:3065/uploads/${item.Product.Images[0].url}`}
                        />
                      }
                      description={
                        <Row Gutter={[8, 16]}>
                          <Col span={24}>{item.Product.description}</Col>
                          <Col span={16}>
                            가격:{' '}
                            <NumberFormat
                              value={item.price}
                              displayType={'text'}
                              thousandSeparator={true}
                            />
                          </Col>
                        </Row>
                      }
                    />
                  }
                />
                <Divider />
                <Row gutter={[8, 16]}>
                  <Col style={{ textAlign: 'right' }} span={24}>
                    총액:{' '}
                    <NumberFormat
                      value={item.price * item.quantity}
                      displayType={'text'}
                      thousandSeparator={true}
                    />
                  </Col>
                </Row>
              </Card>
            </List.Item>
          )}
        />
      )}
      {cart.length > 0 && (
        <Button onClick={onClickPayment} type={'primary'} style={{ width: '100%' }}>
          결제하기
        </Button>
      )}
    </>
  );
};

export default Cart;
