import { Card, Avatar, Button, Col, Row, Descriptions } from "antd";
import { useDispatch, useSelector } from "react-redux";
import {
  ADD_QUANTITY_SUCCESS,
  ADD_QUANTITY_REQUEST,
  SUBSTRACT_QUANTITY_SUCCESS,
} from "../reducers/products";
import NumberFormat from "react-number-format";

const ProductCard = ({ id, title, cover, price, quantity }) => {
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      dispatch({ type: ADD_QUANTITY_SUCCESS, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: SUBSTRACT_QUANTITY_SUCCESS, data: { id } });
  };
  return (
    <Card
      key={id}
      title={title}
      actions={[<div>즉시 구매</div>, <div>장바구니</div>]}
      cover={<img src={cover} />}
    >
      <Card.Meta
        description={
          <Descriptions bordered>
            <Descriptions.Item label={"가격"} span={24}>
              <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />
            </Descriptions.Item>
            <Descriptions.Item label={"수량"} span={24}>
              <Button shape="circle" size="small" onClick={onClickAdd}>
                +
              </Button>
              {quantity}
              <Button shape="circle" size="small" onClick={onClickSubstract}>
                -
              </Button>
            </Descriptions.Item>
            <Descriptions.Item label={"총액"} span={24}>
              <NumberFormat
                value={price * quantity}
                displayType={"text"}
                thousandSeparator={true}
              />
            </Descriptions.Item>
          </Descriptions>
        }
      />
    </Card>
  );
};

export default ProductCard;
