import { useRouter } from "next/router";
import { useSelector, useDispatch } from "react-redux";
import { useState, useEffect } from "react";
import { Card, Button, Modal, List, Comment, Form } from "antd";

const Index = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session } = useSelector((state) => state.user);
  const [modal, setModal] = useState(false);

  const onClickAddShop = () => {
    return setModal((prev) => !prev);
  };

  return (
    <>
      <Modal visible={modal} onCancel={onClickAddShop}></Modal>
      <Card
        title={session ? `${session.name}님의 매장 리스트` : "매장 리스트"}
        extra={<Button onClick={onClickAddShop}>매장추가</Button>}
      >
        {session.Shops.map((shop) => (
          <Card
            title={shop.name}
            onClick={() => router.push(`/shop/admin/${shop.id}`)}
            extra={<Button>매장 삭제</Button>}
          >
            {`주소: ${shop.address} ${shop.addressDetail && shop.addressDetail}`}
          </Card>
        ))}
      </Card>
    </>
  );
};

export default Index;
