import { useRouter } from "next/router";
import { useSelector, useDispatch } from "react-redux";
import { useState, useEffect } from "react";
import { Card, Button, Modal, List, Comment, Form, Input } from "antd";
import { ADD_SHOP_REQUEST } from "../../../reducers/user";
import { useInput } from "../../../components/Generalui/CustomHooks";
import KakaoAddress from "../../../components/Generalui/KakaoAddress";
import AddressDetail from "../../../components/Forms/AddressDetailForm";

const Index = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session, isLoggedIn } = useSelector((state) => state.user);
  const [modal, setModal] = useState(false);
  const [shopName, onChangeShopName] = useInput();
  const [address, setAddress] = useState();
  const [addressDetail, onChangeAddressDetail] = useInput();
  const [coordinates, setCoordinates] = useState();

  if (!session || !isLoggedIn) {
    router.push("/");
  }

  const onClickAddShop = () => {
    return setModal((prev) => !prev);
  };

  const onFinish = () => {
    dispatch({
      type: ADD_SHOP_REQUEST,
      data: {
        UserId: session.id,
        shopName,
        address,
        addressDetail,
        bizcode: session.Shops[0].bizcode,
        coordinates,
      },
    });
    return setModal(false);
  };

  return (
    <>
      <Modal visible={modal} onCancel={onClickAddShop} title={"점포 추가"} footer={null}>
        <Form onFinish={onFinish}>
          <Form.Item rules={[{ required: true, message: "점포명을 입력해주세요" }]}>
            <Input prefix={"* 점포명"} size={"large"} onChange={onChangeShopName} />
          </Form.Item>
          <KakaoAddress address={address} setAddress={setAddress} setCoordinates={setCoordinates} />
          <AddressDetail onChangeAddressDetail={onChangeAddressDetail} />
          <Form.Item>
            <Button type="primary" htmlType="submit" style={{ width: `100%` }}>
              제출
            </Button>
          </Form.Item>
        </Form>
      </Modal>
      <Card
        title={session ? `${session.name}님의 매장 리스트` : "매장 리스트"}
        extra={<Button onClick={onClickAddShop}>매장추가</Button>}
      >
        {session.Shops.map((shop) => (
          <Card
            title={shop.name}
            onClick={() => router.push(`/shop/admin/${shop.id}`)}
            extra={<Button>매장 삭제</Button>}
          >
            {`주소: ${shop.address} ${shop.addressDetail && shop.addressDetail}`}
          </Card>
        ))}
      </Card>
    </>
  );
};

export default Index;
