import { useRouter } from "next/router";
import { useSelector, useDispatch } from "react-redux";
import { useState, useEffect } from "react";
import { Card, Button, Modal, Typography } from "antd";

const Index = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session } = useSelector((state) => state.user);
  const [modal, setModal] = useState(false);
  const onClickAddShop = () => {
    return setModal((prev) => !prev);
  };

  return (
    <>
      <Modal></Modal>
      <Card
        title={session ? `${session.name}님의 매장 리스트` : "매장 리스트"}
        extra={<Button>매장추가</Button>}
      >
        {session.Shops.map((shop) => (
          <Card
            onClick={() => router.push(`/shop/admin/${shop.id}`)}
            extra={<Button>매장 삭제</Button>}
          >
            <Typography.title>{shop.name}</Typography.title>
          </Card>
        ))}
      </Card>
    </>
  );
};

export default Index;
