import { List, Button, Modal, Avatar, Card } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState, useCallback } from 'react';
import { useRouter } from 'next/router';
import { ADMIN_PRODUCTS_REQUEST, DELETE_PRODUCT_REQUEST } from '../../../../reducers/shop';
import PropTypes from 'prop-types';

import EditProduct from '../../../../components/Generalui/EditProduct';

const Console = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session } = useSelector((state) => state.user);
  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  const [edit, setEdit] = useState(false);
  const [product, setProduct] = useState();

  console.log(product && product.title);

  const onToggleEdit = (item) => {
    console.log(item.title);
    setEdit((prev) => !prev);
    setProduct(item);
  };

  const { products } = useSelector((state) => state.shop);

  return (
    <>
      <Modal
        title={'수정하기'}
        visible={edit}
        onCancel={() => {
          setEdit(false);
          setProduct(null);
        }}
        footer={null}
      >
        <EditProduct item={product} setEdit={setEdit} />
      </Modal>
      <List
        itemLayout={'horizontal'}
        dataSource={products}
        renderItem={(item) => {
          return (
            <>
              <List.Item key={item.id}>
                <Card
                  style={{ width: '100%' }}
                  actions={[
                    <div onClick={() => onToggleEdit(item)}>수정</div>, // 수정을 클릭하면 Modal State가 바뀌어서 모달열림
                    <div
                      onClick={() => {
                        const flag = confirm(`이 제품을 정말로 삭제하시겠습니까?`);
                        if (!flag) {
                          return false;
                        } else {
                          dispatch({ type: DELETE_PRODUCT_REQUEST, id: item.id });
                        }
                      }}
                    >
                      삭제
                    </div>,
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                        shape={'square'}
                        size={'large'}
                      />
                    }
                    title={item.title}
                    description={
                      <>
                        <p>
                          {item.price},{item.description}
                        </p>
                      </>
                    }
                  />
                </Card>
              </List.Item>
            </>
          );
        }}
      />
    </>
  );
};

Console.protoType = {
  productList: PropTypes.object.isRequired,
};

export default Console;
