import {
  Card,
  Col,
  Space,
  Row,
  Divider,
  message,
  Typography,
  Modal,
  Tag,
} from 'antd';
import NumberFormat from 'react-number-format';
import AmountToggle from './Generalui/AmountToggle';
import { useDispatch } from 'react-redux';
import { ADD_CART_ITEM_REQUEST, CLEAR_CART_REQUEST } from '../reducers/cart';
import { INITIATE_QUANTITY_REQUEST } from '../reducers/menu';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { PAYMENT_FAILURE, PAYMENT_SUCCESS } from '../reducers/payment';
import { useState } from 'react';
import OneSignal from 'react-onesignal';

const ProductCard = ({ product }) => {
  const [toggleModal, setToggleModal] = useState(false);
  const onChangeToggleModal = () => setToggleModal((prev) => !prev);
  const dispatch = useDispatch();

  const { isLoggedIn, session } = useSelector((state) => state.user);

  const onClickAddCart = () => {
    const discountedProduct = {
      ...product,
      price: product.discount
        ? product.price * (1 - product.discount)
        : product.price,
      amount: product.discount
        ? product.price * (1 - product.discount) * product.quantity
        : product.price * product.quantity,
    };
    dispatch({
      type: ADD_CART_ITEM_REQUEST,
      product: discountedProduct,
    });
    dispatch({ type: INITIATE_QUANTITY_REQUEST, data: product });
  };

  const onWindowScroll = () => {
    window.scrollTo({ top: 0 });
  };

  const onClickPayment = () => {
    if (isLoggedIn) {
      dispatch({ type: CLEAR_CART_REQUEST });

      const originData = {
        UserId: session.id,
        UserName: session.name,
        ProductId: product.id,
        ShopId: product.Shop.id,
        price: product.discount
          ? product.price * (100 - product.discount) * 0.01 * product.quantity
          : product.price * product.quantity,
        quantity: product.quantity,
        ShopName: product.Shop.name,
        ProductTitle: product.title,
      };

      const custom_data = JSON.stringify([originData]);

      onWindowScroll();
      if (IMP !== null) {
        IMP.init(process.env.IAMPORT_KEY);
        const today = Date.now();
        IMP.request_pay(
          {
            pay_method: 'card',
            name: product.title,
            Merchant_uid: 'uid' + today,
            amount: product.Discount
              ? product.price *
                (100 - product.Discount.rate) *
                0.01 *
                product.quantity
              : product.price * product.quantity,
            custom_data,
          },
          callback
        );
      }
    } else {
      onChangeToggleModal();
    }
  };

  const [initialized, setInitialized] = useState(false);
  OneSignal.init({
    appId: 'e403941a-d352-4195-a086-502cb595a147',
    notifyButton: {
      enable: true,
    },
    allowLocalhostAsSecureOrigin: true,
  }).then(() => {
    setInitialized(true);

    OneSignal.getUserId().then((externalUserId) => {
      console.log('externalUserId: ', externalUserId);
    });

    return () => {
      window.OneSignal = undefined;
    };
  });

  const callback = async (response) => {
    const { success, error_msg } = response;
    if (success) {
      axios
        .post(`${process.env.BACKEND_IP}/payments/complete`, { data: response })
        .then((response) => {
          dispatch({ type: PAYMENT_SUCCESS }, { data: response });
        })
        .catch((e) => {
          console.log(e);
          dispatch({ type: PAYMENT_FAILURE }, { error: e });
        });
    } else {
      alert(`결제 실패: ${error_msg}`);
    }
  };

  return (
    <>
      <Modal
        header={null}
        visible={toggleModal}
        onCancel={onChangeToggleModal}
        footer={null}
      >
        로그인을 하셔야합니다.
      </Modal>
      <Card
        key={product.id}
        title={
          <>
            {product.Discount ? (
              <Space>
                <Typography.Title level={3}>{product.title}</Typography.Title>{' '}
                <Tag color={'magenta'}>-{product.Discount.rate}%</Tag>
              </Space>
            ) : (
              <h1>{product.title}</h1>
            )}
          </>
        }
        actions={[
          <div onClick={onClickPayment}>즉시 구매</div>,
          <div onClick={onClickAddCart}>장바구니</div>,
        ]}
        cover={
          <img
            style={{ border: `1px solid #EFF2F5` }}
            src={
              product.Images[0] &&
              `${process.env.BACKEND_IP}/uploads/${product.Images[0].url}`
            }
          />
        }
        hoverable
      >
        <Card.Meta
          description={
            <>
              <Row gutter={[16, 16]} align={'middle'} justify={'end'}>
                <Col md={16} xs={16} style={{ display: 'inline-block' }}>
                  {product.Discount ? (
                    <>
                      <h5>가격</h5>
                      <Space align="center">
                        <h1 style={{ color: '#FF5733', fontWeight: 'bold' }}>
                          <NumberFormat
                            value={
                              product.price *
                              (100 - product.Discount.rate) *
                              0.01
                            }
                            displayType={'text'}
                            thousandSeparator={true}
                          />
                          원
                        </h1>
                        <Tag>
                          <NumberFormat
                            value={product.price}
                            displayType={'text'}
                            thousandSeparator={true}
                          />
                          원
                        </Tag>
                        {'  '}
                      </Space>
                    </>
                  ) : (
                    <div>
                      <h5>가격</h5>
                      <h1>
                        <NumberFormat
                          value={product.price}
                          displayType={'text'}
                          thousandSeparator={true}
                        />
                        원
                      </h1>
                    </div>
                  )}{' '}
                </Col>
                <Col md={8} xs={8}>
                  <AmountToggle id={product.id} quantity={product.quantity} />
                </Col>
              </Row>
              <Divider />
              <Row align={'middle'} justify={'center'}>
                <Col span={8} xs={8}>
                  <div>총 주문금액</div>
                </Col>
                <Col span={16} xs={16}>
                  <div style={{ textAlign: 'right' }}>
                    <h1>
                      <NumberFormat
                        value={
                          product.Discount
                            ? product.price *
                              (100 - product.Discount.rate) *
                              0.01 *
                              product.quantity
                            : product.price * product.quantity
                        }
                        displayType={'text'}
                        thousandSeparator={true}
                      />
                      원
                    </h1>
                  </div>
                </Col>
              </Row>
            </>
          }
        />
      </Card>
    </>
  );
};

export default ProductCard;
