import { Input, Form } from 'antd';
import { useDispatch } from 'react-redux';
import { SEARCH_PRODUCT_SUCCESS } from '../../reducers/menu';
import { useInput } from './CustomHooks';
import {useState} from 'react'

const SearchBar = ({ setToggleSearch }) => {
  const dispatch = useDispatch();
  const [text, onChangeText, setText] = useState();

  const onSearch = () => {
    dispatch({ type: SEARCH_PRODUCT_SUCCESS, data: { text } });
    setText('');
    console.log(text);
    setToggleSearch(false);
  };

  return <Input.Search size={'large'} onChange={onChangeText} enterButton onSearch={onSearch} />;
};

export default SearchBar;
