import { createStore, compose, applyMiddleware } from 'redux';
import { createWrapper } from 'next-redux-wrapper';
import { composeWithDevTools } from 'redux-devtools-extension';
import createSagaMiddleware from 'redux-saga';
import rootSaga from '../sagas';
import rootReducer from '../reducers';
const { persistStore } = require('redux-persist');
import expireIn from 'redux-persist-transform-expire-in';

const configureStore = () => {
  const sagaMiddleware = createSagaMiddleware();

  const middlewares = [sagaMiddleware];
  const enhencer =
    process.env.NODE_ENV === 'production'
      ? compose(applyMiddleware(...middlewares))
      : composeWithDevTools(applyMiddleware(...middlewares));
  const rootStore = (reducer) => createStore(reducer, {}, enhencer);

  const isServer = typeof window === 'undefined';
  if (isServer) {
    const store = rootStore(rootReducer);
    store.sagaTask = sagaMiddleware.run(rootSaga);
    console.log(`store: ${store}`);
    return store;
  } else {
    const { persistReducer } = require('redux-persist');
    const storage = require('redux-persist/lib/storage').default;

    const expiredBy = 60 * 60 * 1000;
    const expirationKey = 'expirationKey';

    const persistConfig = {
      key: 'nextjs',
      whitelist: ['user'],
      storage,
      transforms: [expireIn(expiredBy, expirationKey, {})],
    };
    console.log('-----------------------------------------------------------------------');
    console.log(`store: ${store}`);
    console.log('-----------------------------------------------------------------------');
    const persistedReducer = persistReducer(persistConfig, rootReducer);
    const store = rootStore(persistedReducer);
    store.sagaTask = sagaMiddleware.run(rootSaga);
    store.__persistor = persistStore(store);
    return store;
  }
};

const wrapper = createWrapper(configureStore, { debug: process.env.NODE_ENV !== 'production' });

export default wrapper;
