              </Modal>
               <Card
                  style={{ width: '100%' }}
                  actions={[
                    <div onClick={onToggleEdit}>수정</div>,
                    <div
                      onClick={() => {
                        const flag = confirm(`이 제품을 정말로 삭제하시겠습니까?`);
                        if (!flag) {
                          return false;
                        } else {
                          dispatch({ type: DELETE_PRODUCT_REQUEST, id: item.id });
                        }
                      }}
                    >
                      삭제
                    </div>,
                  ]}
                >
                  <Card.Meta
                    title={item.title}
                    description={
                      <>
                        <p>
                          {item.price},{item.description}
                        </p>
                      </>
                    }
        />
        
                </Card>
          
    </>
  );
};

Console.protoType = {
  productList: PropTypes.object.isRequired,
};

export default Console;
