import { all, put, fork, call, takeLatest } from "redux/effects";
import { LOAD_PRODUCTS_SUCCESS, LOAD_PRODUCTS_FAILURE } from "../reducers/products";

function loadProductApi(loadData) {
  return axios.post("/products/load", loadData);
}

function* loadProducts(action) {
  try {
    const result = yield call(loadProductsApi, action.data);
    yield put({
      type: LOAD_PRODUCTS_SUCCESS,
      data: result.data,
    });
  } catch (e) {
    console.log(e);
    yield put({
      type: LOAD_PRODUCTS_FAILURE,
      error: e,
    });
  }
}

export default function* productsSaga() {
  all([fork()]);
}
