import { Menu } from 'antd';
import { useDispatch } from 'react-redux';
import { SELECT_CATEGORY_SUCCESS } from '../../reducers/menu';
import { CATEGORY_FILTERED_SUCCESS } from '../../reducers/shop';

const MenuNavibarLayout = ({ categories, type, setCategory }) => {
  const dispatch = useDispatch();

  return (
    <Menu mode="horizontal">
      <Menu.Item
        onClick={() => {
          dispatch({
            type: type == 'menu' ? SELECT_CATEGORY_SUCCESS : ADMIN_PRODUCTS_REQUEST,
            id: type == 0,
          });
          return setCategory(0);
        }}
      >
        전체
      </Menu.Item>
      {categories.map((category) => (
        <Menu.Item>
          <a
            onClick={() => {
              dispatch({
                type: type == 'menu' ? SELECT_CATEGORY_SUCCESS : CATEGORY_FILTERED_SUCCESS,
                id: category.id,
              });
              return setCategory(category.id);
            }}
          >
            {category.name}
          </a>
        </Menu.Item>
      ))}
    </Menu>
  );
};

export default MenuNavibarLayout;
