import { List, Button, Avatar, Card } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { END } from "redux-saga";
import { useEffect } from "react";
import { useRouter } from "next/router";
import wrapper from "../../../store/configureStore";
import axios from "axios";
import { ADMIN_PRODUCTS_REQUEST } from "../../../reducers/shop";
import { LOAD_USER_REQUEST } from "../../../reducers/user";
import PropTypes from "prop-types";

const Console = () => {
  const dispatch = useDispatch();
  const Router = useRouter();
  const { session } = useSelector((state) => state.user);
  if (!session || session.division === false) {
    return Router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST });
    }, []);
  }
  const { productList } = useSelector((state) => state.shop);
  return (
    <>
      <List
        itemLayout={"horizontal"}
        dataSource={productList}
        renderItem={(item) => {
          return (
            <List.Item key={item.id}>
              <Card style={{ width: "100%" }} actions={[<div>수정</div>, <div>삭제</div>]}>
                <List.Item.Meta
                  avatar={
                    <Avatar
                      src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                      shape="square"
                      size={100}
                    />
                  }
                  title={<h1>{item.title}</h1>}
                  description={item.description}
                />
              </Card>
            </List.Item>
          );
        }}
      />
    </>
  );
};

Console.protoType = {
  productList: PropTypes.object.isRequired,
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
  const cookie = context.req ? context.req.headers.cookie : "";
  axios.defaults.headers.Cookie = "";
  if (context.req && cookie) {
    axios.defaults.headers.Cookie = cookie;
  }
  context.store.dispatch({ type: LOAD_USER_REQUEST });
  context.store.dispatch(END);
  await context.store.sagaTask.toPromise();
});

export default Console;
