import React, { useEffect, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Form, Input, Button, message } from 'antd';
import { MailOutlined, LockOutlined } from '@ant-design/icons';
import { useInput } from '../../components/Generalui/CustomHooks';
import { LOG_IN_REQUEST } from '../../reducers/user';

const Login = () => {
  const dispatch = useDispatch();
  const [email, onChangeEmail] = useInput();
  const [password, onChangePassword] = useInput();

  const { loginError } = useSelector((state) => state.user);

  const onSubmit = useCallback(() => {
    dispatch({
      type: LOG_IN_REQUEST,
      data: {
        email,
        password,
      },
    });
  }, [email, password]);

  message.config({ top: '50%' });

  useEffect(() => {
    if (loginError) {
      message.error(loginError.message);
    }
  }, [loginError]);

  return (
    <Form onFinish={onSubmit} style={{ verticalAlign: 'middle' }}>
      <Form.Item
        name={'email'}
        rules={[
          { required: true, message: '이메일을 입력해주세요' },
          { type: 'email', message: '형식에 맞지 않는 아이디입니다' },
        ]}
      >
        <Input prefix={<MailOutlined />} name={'email'} value={email} onChange={onChangeEmail} />
      </Form.Item>
      <Form.Item
        name={'password'}
        rules={[
          { required: true, message: '비밀번호를 입력해주세요' },
          { pattern: /^[0-9a-zA-Z]{6,12}$/, message: '형식에 맞지 않는 비밀번호입니다' },
        ]}
      >
        <Input.Password
          name={'password'}
          value={password}
          prefix={<LockOutlined />}
          onChange={onChangePassword}
        />
      </Form.Item>

      <Button htmlType={'submit'} type={'primary'} width={'100%'}>
        로그인
      </Button>
    </Form>
  );
};

export default Login;
