import { List, Avatar, Card, Empty, Rate } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useChecked } from '../../components/Generalui/CustomHooks';

import NumberFormat from 'react-number-format';
import { useEffect } from 'react';
import { useWindowSize } from '@react-hook/window-size';
import axios from 'axios';
import { useRouter } from 'next/router';
import {
  CLIENT_HISTORY_REMOVE_REQUEST,
  LOAD_PAYMENT_HISTORY_REQUEST,
  RATING_REQUEST,
} from '../../reducers/payment';

const History = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const [toggleRating, onToggleRating, setToggleRating] = useChecked(false);

  const { isLoggedIn, session } = useSelector((state) => state.user);
  const { history } = useSelector((state) => state.payment);
  useEffect(() => {
    dispatch({ type: LOAD_PAYMENT_HISTORY_REQUEST, division: session.division, id: session.id });
  }, []);

  if (!isLoggedIn) {
    router.back();
  }

  const [windowWidth, windowHeight] = useWindowSize();

  const onWindowScroll = () => {
    window.scrollTo({ top: 0 });
  };

  return (
    <>
      {history.length === 0 ? (
        <Card style={{ height: '100vh' }}>
          <div style={{ marginTop: windowHeight * 0.3, textAlign: 'center' }}>
            <Empty />
            <h1>거래내역이 존재하지 않습니다</h1>
          </div>
        </Card>
      ) : (
        <List
          itemLayout="horizontal"
          dataSource={history}
          renderItem={(item) => {
            const date = new Date(item.createdAt);
            const localeDate = date.toLocaleString();

            const onChangeRating = (value) => {
              dispatch({
                type: RATING_REQUEST,
                data: {
                  PaymentsId: item.id,
                  UserId: session.id,
                  rate: value,
                  ProductId: item.Product.id,
                },
              });
            };

            const onClickRemoveHistory = () => {
              dispatch({
                type: CLIENT_HISTORY_REMOVE_REQUEST,
                UserId: session.id,
                PaymentId: item.id,
              });
            };

            return (
              <List.Item>
                <Card
                  style={{ width: '100%' }}
                  actions={[<div>결제취소</div>]}
                  extra={item.status ? '결제완료' : '취소요청'}
                >
                  <List.Item.Meta
                    title={
                      <>
                        {item.Shop.name}_{item.Product.title}_{localeDate}
                      </>
                    }
                    description={
                      <>
                        <p>
                          결제수량:{' '}
                          <NumberFormat
                            value={item.quantity}
                            displayType={'text'}
                            thousandSeparator={true}
                          />
                          개, 결제단가:{' '}
                          <NumberFormat
                            value={item.price}
                            displayType={'text'}
                            thousandSeparator={true}
                          />
                          원
                        </p>
                        <p>
                          결제금액:{' '}
                          <NumberFormat
                            value={item.price * item.quantity}
                            displayType={'text'}
                            thousandSeparator={true}
                          />
                          원
                        </p>
                        <Rate
                          defaultValue={item.Rating && item.Rating.rate ? item.Rating.rate : null}
                          onChange={onChangeRating}
                        />
                      </>
                    }
                    avatar={
                      <Avatar
                        size={50}
                        shape="square"
                        src={`http://127.0.0.1:3065/uploads/${item.Product.Images[0].url}`}
                      />
                    }
                  />
                </Card>
              </List.Item>
            );
          }}
        />
      )}
    </>
  );
};

export default History;
