import React from "react";
import Head from "next/head";
import { createStore, applyMiddleware, compose } from "redux";
import createSagaMiddleware from "redux-saga";
import { MakeStore, createWrapper, Context } from "next-redux-wrapper";
import reducer from "../reducers";
import rootSaga from "../sagas";

const App = ({ Component, store, pageProps }) => {
  return (
    <>
      <Head>
        <title>NodeBird</title>
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/antd/4.2.4/antd.compact.css"
        />
        <script type="text/javascript" src="https://code.jquery.com/jquery-1.12.4.min.js"></script>
        <script
          type="text/javascript"
          src="https://cdn.iamport.kr/js/iamport.payment-1.1.5.js"
        ></script>
        <style>
          {`body {
            background-color: #EFF2F5!important
          }`}
        </style>
      </Head>
      <Component {...pageProps} />
    </>
  );
};

App.getInitialProps = (context) => {
  let pageProps = {};
  const { ctx, Component } = context;
  if (Component.getInitialProps) {
    pageProps = Component.getInitialProps(ctx);
  }
  return pageProps;
};

const configureStore = (initialState, options) => {
  const sagaMiddleware = createSagaMiddleware();

  const middleWares = [sagaMiddleware];
  const enhencer =
    process.env.NODE_ENV === "production"
      ? compose(applyMiddleware(...middleWares))
      : compose(
          applyMiddleware(...middleWares),
          !options.isServer && window.__REDUX_DEVTOOLS_EXTESION__ !== "undefined"
            ? window.__REDUX_DEVTOOLS_EXTENSION__()
            : (f) => f
        );
  // const store = createStore(reducer, initialState);
  sagaMiddleware.run(rootSaga);
  return store;
};

export const makeStore = (Context) => {
  const sagaMiddleware = createSagaMiddleware();

  // 2: Add an extra parameter for applying middleware:
  const store = createStore(reducer, applyMiddleware(sagaMiddleware));

  // 3: Run your sagas on server
  store.sagaTask = sagaMiddleware.run(rootSaga);

  // 4: now return the store:
  return store;
};

export default createWrapper(configureStore)(App);
