import {
  Button,
  Form,
  Input,
  Descriptions,
  Checkbox,
  Card,
  Space,
  Collapse,
  Switch,
  Divider,
} from "antd";
import { useState, useCallback } from "react";
import { useDispatch } from "react-redux";
import { useInput } from "../../components/Generalui/CustomHooks";
import { SIGN_UP_REQUEST } from "../../reducers/user";
import Router, { withRouter } from "next/router";

const Signup = () => {
  const [email, onChangeEmail] = useInput();
  const [password, onChangePassword] = useInput();
  const [passwordCheck, setPasswordCheck] = useState();
  const [passwordError, setPasswordError] = useState(false);
  const [name, onChangeName] = useInput();
  const [bizcode, setBizcode] = useState();
  const [termToggle, setTermToggle] = useState(false);
  const [divisionToggle, setDivisionToggle] = useState(false);
  const dispatch = useDispatch();

  const onChangePasswordCheck = (e) => {
    setPasswordCheck(e.target.value);
    setPasswordError(password != e.target.value);
  };

  const onClickTermToggle = () => {
    return setTermToggle(!termToggle);
  };

  const onSubmitForm = useCallback(
    (e) => {
      if (passwordError) {
        return setPasswordError(true);
      } else {
        dispatch({
          type: SIGN_UP_REQUEST,
          data: {
            username: email,
            password: password,
          },
        });
      }
    },
    [email, password, passwordCheck]
  );

  const onChangeDivisionToggle = () => {
    return setDivisionToggle(!divisionToggle);
  };

  const onChangeBizcode = (e) => {
    console.log(e.target.value);
    setBizcode(e.target.value);
  };

  const bizcodeConfirm = (e) => {
    let value = e.target.value;
    if (!value.match(/\d{1}/g) || value.length != 10) {
      return setBizcode("");
    }
    let sum = 0,
      key = [1, 3, 7, 1, 3, 7, 1, 3, 5];
    for (let i = 0; i < 9; i++) {
      sum += key[i] * Number(value[i]);
    }

    return 10 - ((sum + Math.floor((key[8] * Number(value[8])) / 10)) % 10) == Number(value[9]);
  };

  return (
    <>
      <Card
        title={
          <Space direction="horizental" align={`baseline`} size={`large`}>
            {divisionToggle ? <h1>가맹점</h1> : <h1>일반회원</h1>}
            <Switch
              onClick={onChangeDivisionToggle}
              checkedChildren={`가맹점`}
              unCheckedChildren={`일반회원`}
              size={"middle"}
            />
          </Space>
        }
      >
        <Form onFinish={onSubmitForm}>
          <Space direction="vertical" size={"large"} style={{ width: "100%", height: `100vh` }}>
            {!termToggle && (
              <Descriptions bordered column={24} layout={`vertical`} size={`large`}>
                <Descriptions.Item label="필수">개인(신용)정보수집/이용</Descriptions.Item>
                <Descriptions.Item label="필수">고유식별정보의 처리</Descriptions.Item>
                <Descriptions.Item label="필수">통신사 이용약관</Descriptions.Item>
                <Descriptions.Item label="필수">서비스 이용약관</Descriptions.Item>
              </Descriptions>
            )}
            <div style={{ textAlign: "right" }}>
              <Checkbox onChange={onClickTermToggle}>전체동의</Checkbox>
            </div>
            <div>
              <Form.Item
                name="email"
                rules={[
                  {
                    type: "email",
                    message: "올바른 이메일 형식을 입력해주세요!",
                  },
                  {
                    required: true,
                    message: "이메일를 입력해 주세요!",
                  },
                ]}
              >
                <Input
                  size={`large`}
                  prefix={`* Email`}
                  value={email}
                  onChange={onChangeEmail}
                  placeholder="Email을 입력해주세요"
                />
              </Form.Item>
              <Form.Item
                name="password"
                rules={[
                  {
                    required: true,
                    message: "비밀번호를 확인해주세요",
                  },
                ]}
              >
                <Input.Password
                  prefix={`* 비밀번호`}
                  size={`large`}
                  value={password}
                  onChange={onChangePassword}
                  placeholder="비밀번호를 입력해주세요"
                />
              </Form.Item>
              <Form.Item
                name="passwordCheck"
                rules={[
                  {
                    required: true,
                    message: "비밀번호를 확인해 주세요",
                  },
                ]}
              >
                <Input.Password
                  prefix={`* 비밀번호 확인`}
                  size={`large`}
                  value={passwordCheck}
                  onChange={onChangePasswordCheck}
                  placeholder="다시 비밀번호를 입력해주세요"
                />
              </Form.Item>
              {passwordError && <div>비밀번호가 일치하지 않습니다</div>}
              <Form.Item name="name" rules={[{ required: true, message: "이름을 입력해주세요" }]}>
                <Input
                  prefix={divisionToggle ? "* 가게명" : "* 이름"}
                  size={`large`}
                  onChange={onChangeName}
                  value={name}
                />
              </Form.Item>
              {divisionToggle && (
                <Form.Item
                  name="bizcode"
                  rules={[{ required: true, message: "이름을 입력해주세요" }]}
                >
                  <Input
                    prefix={"* 사업자등록번호"}
                    size={`large`}
                    onChange={onChangeBizcode}
                    value={bizcode}
                    onBlur={bizcodeConfirm}
                  />
                </Form.Item>
              )}
            </div>
            <Button htmlType={"submit"} type={"primary"} style={{ width: "100%" }}>
              회원가입
            </Button>
          </Space>
        </Form>
      </Card>
    </>
  );
};

export default withRouter(Signup);
